import java.util.*;

class DuplicateEncoder {

    //Step 1. Create an empty list and fill it with the characters from the given word.
    public static List<Character> wordToList (String word){
        ArrayList<Character> listOfCharacters = new ArrayList<Character>();
        for (int i = 0; i < word.length(); i++) {
            listOfCharacters.add(word.charAt(i));
        }
        return listOfCharacters;
    }

    //Step 2. Create a method which will check is the character duplicated
    public static boolean duplicatesExist (String word, String character) {
        Integer num = word.indexOf(character);
        System.out.println(num);
        if (num == -1 ){
            return false;
        }
        return true;
    }

    //Step 3. Create a method which will exclude the character for the given index
    public static List<Character> newList (String word, Integer index) {
        List<Character> oldList = wordToList(word);
        ArrayList<Character> newListOfCharacters = new ArrayList<Character>();
        for(int i = 0; i < oldList.size(); i++){
            if (index != i) {
                newListOfCharacters.add(oldList.get(i));
            }
        }
        return newListOfCharacters;
    }

    //Step 4. Create a method which will convert List of carracters to String.
    public static String charrecterListToString (List<Character> testList) {
        StringBuilder builder = new StringBuilder();
        for (int i = 0; i < testList.size(); i++){
            builder.append(testList.get(i));
        }
        return builder.toString();
    }

    //Step 5. Compare each character from given word with the new list where needed index was excluded
    public static String encode (String word) {
        String uppercaseWord = word.toUpperCase();
        List<Character> listOfCharactersFromGivenWord = wordToList(uppercaseWord);
        List<Character> convertedCharacterList = new ArrayList<Character>();
        for (int i = 0; i < listOfCharactersFromGivenWord.size(); i++){
            List<Character> listWithExcludedCharacter = new ArrayList<Character>(newList(uppercaseWord, i));
            Character character = listOfCharactersFromGivenWord.get(i);
            boolean doesListContain = listWithExcludedCharacter.contains(character);
            if (doesListContain) {
                convertedCharacterList.add(')');
            } else {
                convertedCharacterList.add('(');
            }
        }
        String finalConvertedResult = charrecterListToString(convertedCharacterList);
        return finalConvertedResult;
    }

    public static void main(String[] args) {
        String word = "";
        Long start = System.currentTimeMillis();
        System.out.println(encode(word));
        System.out.println(System.currentTimeMillis() - start);

    }

}